css:
@-moz-document domain("goodreads.com") {

body, .mainContent  {
    background: url("https://i.imgur.com/gZ1F794.jpg") no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}
@media (min-width: 1200px) {
    main {
        width: 1350px;
    }
    .homeSecondaryColumn, .homeTertiaryColumn {
        width: 350px;
    }
    .homePrimaryColumn {
        width: 550px;
    }
}
.gr-newsfeedItem, .gr-homePageRailContainer, .showForLargeWidth, .gr-footer__siteLinks, .mainContentFloat, .siteAnnouncement, .gr-childNewsfeedItem__header, .aboutWrapper, .gr-container-fluid {
    background-color: #fff;
    padding: 20px;
    border-radius: 2px;
    border: none;
    margin-top: 8px;
    box-shadow: 2px 2px 5px rgba(0, 0, 0, 0.3);
}
.mainContent {
    margin-top: 10px;
}
.gr-childNewsfeedItem__header {
    margin-bottom: 5px;
    padding: 8px;
}
.gr-newsfeedItem__headerIcon {
    margin-left: -35px;
}

#coverImage {
    border-radius: 2px;
    box-shadow: 2px 2px 5px rgba(0, 0, 0, 0.3);
}
.gr-childNewsfeedItem__header:after {
    display: none;
}
.likeInformation, .gr-commentForm, .brownBox, .gr-comments {
    background-color: #fff;
    border-top: none;
}
.mainContentFloat {
    padding: 30px;
}
}
