css:
@-moz-document domain("web.whatsapp.com") {
/*===========================================================
=					Whatsapp - Easy Arabic					=
=============================================================

  @author: Amer Bakeer
  @date: 03 Nov 2017

*/


/* Enlarge App Size: ************************************** */
/* Please remove it if you do not like it */
.app-wrapper-web .app
{
    width: 100%;
    height: calc(100% - 1px);
    margin: 0 auto;
    top: 0px;
    border-radius: 0px;
}

/* Remove Text Selection Restriction: ********************* */
/* Useful to be able to copy any text easily */
body
{
    user-select: all;
}

/* Set the Arabic Font: *********************************** */
/* 'Segoe UI' is nice for both of Arabic and English */
*
{
    font-family: 'Segoe UI', 'Droid Arabic Naskh', 'Tahoma' !important;
}

/* Small fixes: ******************************************* */
/* Enhancing the Input Text: */
.pluggable-input.pluggable-input-compose .pluggable-input-body
{
    line-height: 24px;
    min-height: 26px;
    max-height: 150px;
    unicode-bidi: plaintext;
}

/* Chat Message: */
.message-chat
{
    max-width: 80%;
    unicode-bidi: plaintext;
}
.message-text span {
    unicode-bidi: plaintext;
}

/* Chat & Header Title: */
.chat-title,
.header-title
{
    line-height: 24px;
}
/* ******************************************************** */
/* Some UI fixes: */
.bubble-audio-has-author
{
    padding-top: 44px;
}

.m6ZEb {
    display: none;
}

/* ******************************************************** */
}
