css:
@-moz-document domain("vk.com") {
body {
background: url(http://shalena.net.ua/wp-content/zimnie/golub120.gif);
background-attachment: fixed;
font-family: "MS Sans Serif";
color: black;
}
h2 {
color: mediumblue;
}
a {
text-decoration: none;
color: blue;
}
a:visited {
color: blue;
}
a:active {
color: darkblue;
}
.top_profile_name {
color: white;
}
.back {
box-shadow: blue 0 0 70px;
}
.owner_photo_wrap {
box-shadow: lightblue 0 0 20px;
}
.page_block {
background: Azure;
box-shadow: lightblue 0 0 20px;
}
.page_block img {
border-radius: 0;
box-shadow: lightblue 0 0 20px;
}
.page_add_media {
background: Azure;
}
.sumbit_post, .clear_fix {
background: Azure;
}
#post_field {
background: Azure;
}
#profile_friends img {
border-radius: 0;
box-shadow: lightblue 0 0 20px;
}
#profile_friends_online img {
border-radius: 0;
box-shadow: lightblue 0 0 20px;
}
#top_profile_link img {
border-radius: 0;
}
.chat_onl_inner img {
border-radius: 0;
}

}
