css:
@-moz-document url-prefix("http://www.jeuxvideo.com/gaming_live/") {

/* Extended playlist */
#playlist {
    height: 100% !important;
    overflow: hidden !important;
}

/* Removing tag */
.versions {
   display: none !important;
}

/* Centered items */
#td_player {
   position: absolute !important;
   left: 0 !important;
   width: 66% !important;
/* For widescreen resolution, delete the width above and uncomment this one
   width: 79.2% !important;*/
}
#td_playlist {
   position: absolute !important;
   right: 0 !important;
   height: 100% !important;
}

/* Tweaking */
#td_player > div > h2 {
   margin-top: 4px !important;
   border-top: 4px white dashed !important;
   padding: 3px !important;
   margin-top: 12px !important;
}
#lien_fiche {
   float: right !important;
}
}
