css:
@-moz-document domain("bitcointalk.org")
{
body {
    background: #2F3238;
}
.smalltext a:link {
    color: #fff;
}
td,
th,
tr {
    color: #fff;
}
.subject a {
    color: #2ab0ba !important;
}
.poster_info a {
    color: #2ab0ba !important;
}
.td_buttons a {
    color: #2ab0ba !important;
}
.quote {
    color: #fff;
    background-color: #2F3238;
    border: 1px solid #6765c099;
}
.trustscore {
    color: white !important;
}
.tborder {
    background-color: transparent;
    border: 0px;
}
.nav,
.nav:link,
.nav:visited {
    color: #fff;
    transition: all 0.2s linear;
}
div#bodyarea .maintab_back a:link,
div#bodyarea .maintab_back a:visited,
div#bodyarea .mirrortab_back a:link,
div#bodyarea .mirrortab_back a:visited {
    color: #2ab0ba;
}
a.nav:hover {
    color: #2ab0ba;
}
a:link {
    color: #2ab0ba;
}
.titlebg,
.titlebg a:link,
.titlebg a:visited {
    color: white;
}
.windowbg,
#preview_body {
    color: #fff;
}
.windowbg a:link {
    color: #fff;
}
.windowbg3 {
    color: #fff;
}
.maintab_back a:hover,
.maintab_active_back a:hover {
    color: #2ab0ba;
}
.catbg > img {
    display: none;
}
.catbg,
.catbg2,
.catbg3 {
    border-bottom: solid 0.1em #37557680;
}
.titlebg2 {
    color: #fff;
}
.titlebg2 a:link {
    color: #2ab0ba;
}
.windowbg2 {
    color: #ffffff;
    background-color: #26292E;
}
.windowbg2 a {
    color: #2ab0ba;
}
.bordercolor {
    background-color: #3C5D6F;
}
.maintab_first {
    background: #26292E;
}
.maintab_active_first {
    background: #26292E;
}
.maintab_back,
.maintab_active_back {
    background: #26292E;
}
.maintab_active_last {
    background: #26292E;
}
.maintab_last {
    background: #26292E;
}
.catbg,
tr.catbg td,
.catbg3,
tr.catbg3 td,
.catbg2 {
    background: #26292E;
}
.titlebg2 {
    background: #26292E;
    border-bottom: 0;
    border-top: 0;
}
.tborder {
    padding: 0px;
}
.windowbg,
#preview_body,
.windowbg3 {
    background-color: #26292E;
}
.titlebg,
tr.titlebg th,
tr.titlebg td,
.titlebg2,
tr.titlebg2 th,
tr.titlebg2 td {
    color: white;
    border-bottom: 0px;
    border-top: 0px;
    background: #26292E;
}
#footerarea {
    display: none;
}

}
