css:
@-moz-document domain("www.rottentomatoes.com") {
/*Ads*/

#header_and_leaderboard,
#aside,
.adColumn,
.col-sm-7,
.sleaderboard_wrapper,
#medrec_top_ad,
.medrec_ad {
    display: none !important;
}
#header-main,
.header_main {
	margin-top: 0px !important;
}
/* Centered index content*/

.col-sm-17 {
    width: 100% !important;
}
/* Feature alignment */

.headline {
    padding: 0px !important;
}
/*Removed clutter*/

.trendingBar,
.newsList,
#header-top-bar-critics,
#header-whats-the-tomatometer,
#navbar_brand,
.navbar-header,
#navigation-column {
    display: none !important;
}
/*nav bar alignment*/

.navbar-searchbox.main {
    width: 90% !important;
}
.col-sm-13,
.col-sm-11 {
    width: 50% !important;
}
.navbar-nav {
    padding-top: 26px !important;
}
/*Pages content */

#content-column {
    padding-left: 2% !important;
}
.col-center-right {
    width: 98% !important;
}
}

@-moz-document url("https://www.rottentomatoes.com/browse/box-office/") {
/*box office page*/
.col-right{
    display: none !important;
}
.col-left-center {
	width: 100% !important;
}
}
