css:
@-moz-document domain("www.aviationexam.com") {
body {
    background: #111;
    color: #999;
}
#Panel_AnsBasic tr td,
.qs-comm-tx,
.qs-comm-ic,
.text_gg_14 {
    background-color: #222!important;
    color: #999;
    border-bottom: 1px solid #000;
    border-top: 1px solid #444;
    border-right: none;
    text-shadow: 2px 2px 3px #000000;
}
.qs-comm-ic {
    z-index: 50;
    position: relative
}
.tr-ans-wr td {
    color: #ab0202!important
}
.tr-ans-corr td {
    color: green!important
}
.qsb-gen,
a#B_Finish,
.button.button-wide,
.fancySelect span,
.fancySelect input[type=text],
.fancySelect-low span,
.fancySelect-low input[type=text],
.lowProfileFancySelect span,
.lowProfileFancySelect input[type=text] {
    background: #222;
    border-width: 1px;
    border-style: solid;
    border-color: #444 #444 #000 #000;
}
.qsb-ans-wr {
    color: #ab0202!important
}
.qsb-ans-cor {
    color: green!important
}
.figthumb a {
    opacity: 0.3
}
.textboxm {
    background: #000;
    border-width: 1px;
    border-style: solid;
    border-color: #222 #222 #444 #444;
}
a.dash-panel:link,
a.dash-panel:visited {
    color: #777;
}
.dash-icon-round {
    background-color: #000
}
.progscore-row {
    background: #222;
}
.repRow {
    background: #222!important;
    COLOR: #aaa;
}
.repRowAlternate {
    background-color: #444!important;
    color: #aaa;
}
#Panel_Areas label {
    color: #000;
}
.lbl-superRadio label {
    color: #000;
    text-shadow: none;
}
}
