css:
@-moz-document domain("bitcointalk.org")
{

body {
    background-color: #FFF;
}
.catbg > img {
    display: none;
}
.tborder {
    border: 1px solid #fff;
}
.bordercolor {
    background-color: #FFF;
}
.catbg, tr.catbg td, .catbg3, tr.catbg3 td {
    background: #f6f6f6;
    color: #26292E;
}
.catbg2 a:link {
    color: #26292E;
}
.catbg, .catbg2, .catbg3 {
    border-bottom: solid 1px #fff;
}
.catbg2, tr.catbg2 td {
    background: #f6f6f6;
    color: #ffffff;
}
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td {
    color: #26292E;
    background: #f6f6f6;
    border-bottom: 0px;
    border-top: 0px;
}
.windowbg, #preview_body {
    background-color: #f6f6f6;
}
.windowbg3 {
    background-color: #fff;
}
.maintab_back a:link, .maintab_back a:visited, .maintab_active_back a:link, .maintab_active_back a:visited {
    color: #26292E;
}
.maintab_first {
    background: #f6f6f6;
}
.maintab_active_first {
    background: #f6f6f6;
}
.maintab_back,
.maintab_active_back {
    background: #f6f6f6;
}
.maintab_active_last {
    background: #f6f6f6;
}
.maintab_last {
    background: #f6f6f6;
}

.navPages {
    color: #000 !important;
}

#footerarea {
    display: none;
}

}
