css:
@-moz-document domain("www.cpokemon.com") {
body {
    background-color: #161616 !important;
    color: #ededed;
}
#container {
    background-color: #161616 !important;
}
.miniextracto {
    color: #ededed !important;
}
#menu_cont {
    background-color: #2d2d2d;
}
#spotlight {
    background: #2d2d2d;
}
footer.footer-ncp2 {
    color: #f5f5f5;
    background-color: #2d2d2d;
}
.curio_refresher {
    float: right;
    border: 1px solid #2d2d2d;
    padding: 0 10px;
    background-color: rgba(0, 0, 0, 0.2);
    border-radius: 3px;
    cursor: pointer;
}
#curiosidad_cp .titulo {
    background: #2d2d2d;
}
#curiosidad_cp {
    background: #2d2d2d;
}
.post_datos {
    background: #2d2d2d;
}
.comment_selector {
    background: #2d2d2d!important;
}
.single-post .span-16 {
    background: #161616;
}
.wv5_titulo {
    background-color: #2d2d2d;
}
.card {
    background: #2d2d2d!important;
    color: #ededed!important;
}
.card .action span {
    color: #fff;
}
.poke-evento .poke-titulo {
    color: #efefef;
}
.fts-jal-fb-user-name a {
    color: #efefef;
}
#hoy {
    border-width: 0px;
    border-style: solid;
    border-color: rgb(95, 4, 4);
}
.cont-anuncio-sm .inner {
    background: #2d2d2d;
}
#categorizador {
    background: #2d2d2d !important;
}
.ncp_titulo h3,
.title,
.title a {
    color: #efefef;
    -webkit-transition: .2s;
    /* Safari */
    
    transition: .2s;
}
#content {
    color: #ededed !important;
}
.cont-anuncio-sm .inner {
    border: 4px solid rgba(255, 255, 255, 0.32);
}
.ncp_titulo {
    border-bottom: 3px solid rgba(255, 255, 255, 0.32);
}
.ncp2_boton {
    background: rgba(255, 255, 255, 0.32);
}
.ncp2_boton.white {
    border: 1px solid rgba(255, 255, 255, 0.32);
    color: #ededed!important;
    background: #161616;
}
}
