css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("gamefaqs.com") {
    /* Links */
    
    a, #content .content_qna fieldset.searchbox input.submit_search {
        color: #9a9a9a!important;
        /*03C */
        /*f751a  */
    }
    #9a9a9a
    /* Visited Links */
    
    #main_col .today .head .date,
    a:visited {
        color: #ff7b00 !important;
        /* #ff0000 */
    }
    /* Body background and text color*/
    
    #masthead,
    #content,
    html,
    input[type="password"],
    input[type="text"],
    textarea,
    select,
    .pod,
    ul.content_nav li.hi,
    body,
    #platformlist .nav b,
    #content .head h1 {
        background: #171717 !important;
        color: #b6b6b6 !important;
    }
    table.topics,
    .ffaq h3 {
        color: inherit !important;
    }
    table.board tr:not([class*="friend"]) td:not([class*="friend"]),
    table.board th,
    tbody.even td,
    tbody.even th,
    tr.even:not([class*="friend"]) td:not([class*="friend"]),
    tr.even th {
        background-color: #171717 !important;
    }
    table.board tr.even:not([class*="friend"]) td:not([class*="friend"]),
    table.board tr.even th {
        background-color: #171717 !important;
    }
    #content thead th,
    #content tr.head th {
        background: #171717 !important;
        color: #FFF !important;
        font-size: 11px !important;
        padding: 2px 5px !important;
        text-align: left !important;
    }
    table.topics {
        color: #B0E0E6 !important;
    }
    div.content_nav h1 {
        color: #171717 !important;
    }
    /* SEARCH TOPICS */
    
    .searchtopics {
        background-color: #171717 !important;
    }
    .searchtopics .data span {
        background-color: #171717 !important;
        border: 1px solid #444 !important;
    }
    @-moz-document regexp("^http://www.gamefaqs.com/boards/.*") {
        div.span8 {
            width: 100% !important;
        }
        aside.span4 {
            display: none !important;
        }
    }
}
