/* The Code! */
#Head {
    margin: auto;
    width: 960px;
    color: white;
    background: #38ABE3;
}
#Body:after {
    /* clearfix */
    
    visibility: hidden;
    display: block;
    content: "";
    clear: both;
    height: 0;
}
img {
    visibility: hidden;
    display: block;
    content: "";
    clear: both;
}
}
