css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
  body, td, .admin td, .subtext td, input[type="submit"], 
  .default, .admin, .title, .adtitle, .subtext,
  .yclinks, .pagetop, .comhead, .comment, .dead
  { font-family: sans-serif !important }

  input, textarea { font-family: monospace !important }

  textarea[name=text] {
    font-size: 9pt !important;
    height: 20em !important;
  }
}
