css:
@-moz-document url-prefix('http://master194.com/forum/') {
                body {
                    background-color: #000;
                    color:#fff; /* texte */
                }

                #logodesc {
                    background: #000;   /* pub */
                }

                .cat {
                    background-color: #000;
                }

                .row1, .row2 {
                    background-color: #000; /* lignes tableaux  */
                }
                .row3 {
                    background-color: #050505cc; /* "annonce, sujet" etc */
                }

                .cat {
                    background-color: #050505cc; /* titres des index forums */
                    background-image: none;
                }
                .catdiv {
                    background: #050505cc; /* case bizarre isolé */ 
                }

                .spacer {
                    background-color: #000;
                }

                a:link {
                    color: #009be7;
                }

                a:visited {
                    color: #a4e1ff
                }
}
