css:
@-moz-document domain(parahumans.net) {

  body, .site
  {
    background: #FCFCFC !important;
  }
  .content-area {
    font-family: Cabin !important;
    color: #434343 !important;
    font-size: 11pt;
  }
  .header-image > * > img {
    height: 120px;
    width: 600px;
    position: relative;
    left: 30%;
  }
  li > a, span > a
  {
    font-size: 1.2em;
  }
  a:link, a:visited
  {
    color: #7F98D4 !important;
  }
  a:hover
  {
    color: #434343 !important;
  }
}
