@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("addons.mozilla.org") {

 a.AddonMoreInfo-version-history-link { float: left; font-size: 2em;}

 .AddonMeta { position: absolute; top: 2%; right: 10%; z-index: 999; }
 .AddonMoreInfo-dl { position: absolute; top: 23%; right: 10%; z-index: 999; }
/* .ExpandableCard-contents { float: right;  position: absolute; top: 2%; right: 10%; z-index: 999; } */ /* broken */

 .SearchFilters-AddonType-label, #SearchFilters-AddonType, .SearchFilters-OperatingSystem-label, #SearchFilters-OperatingSystem { display: none; }
 .Header-user-and-external-links, .Header-authenticate-button, .SearchForm, div.Header-content   { display: none; } 
 .AddonMoreInfo-privacy-policy-title, .AddonMoreInfo-privacy-policy-link, .DropdownMenu { display: none; }
 .AddonMoreInfo-stats-link, .AddonMoreInfo-stats-title, .__fx-devtools-hide-shortcut__ { display: none; }
 .AddonMoreInfo-license-title, .AddonMoreInfo-license-link, .AddonMoreInfo-links-title { display: none; }
 section.Card:nth-child(3) > h2:nth-child(1), .ReportAbuseButton--preview, .Footer { display: none; }
 .Addon-overall-rating > h2:nth-child(1), .Addon-overall-rating > div:nth-child(2) { display: none; }
 #SearchFilters-Featured, label.SearchFilters-label:nth-child(7) { display: none; }
 section.Card:nth-child(2) > h2:nth-child(1) { display: none; }  /* defunc filterResultsToggle */
 .CardList > h2:nth-child(1) { display: none; } /* searchListTitle "searchResults" */

 .Card--photon .Card-contents { padding: 0; }

 .AddAddonToCollection { display: none; }
 .PermissionsCard { display: none; }

 }
