css:
@-moz-document url-prefix("http://chat.thecolorless.net") {
#header {
    top: 0;
    height: 125px;
    background: #99ddff; /* Header Bar bg color*/
    opacity: 0.5;
    border-bottom: 1px solid #000;
    color: #111;
    text-shadow: #ccc 1px 1px 0;
}

body {
    background-image:url("https://i.imgur.com/3J6mk1C.png"); /*Background Image*/
    background-attachment: fixed;
    color: #fff;
    text-shadow: #000 1px 1px 0;
    font-size: 12px;
    line-height: 18px;
    font-family: "Helvetica Neue", Helvetica, Arial, Verdana, sans-serif;
    overflow-y: scroll;
}
#radio{
    opacity: 0.4;
}
}
