css:
@-moz-document url-prefix("https://github.ugent.be/") {
/* GENERAL */
* {
    border-radius: 0 !important;
}

.repohead,
.filter-bar,
.table-list-header,
.file-header {
    background-color: #e9f0fa !important;
}

/* HEADER */
.Header:not(.gist-header) .header-logo-subbrand:after {
    content: "UGent";
    display: block;
    position: absolute;
    top: 22px;
}

.header-logo-subbrand {
    margin-top: -3px;
}

.Header {
    background-color: #1E64C8;
}

/* buttons */
.btn-primary,
.btn-primary:hover,
.btn-primary:active,
.btn-primary.selected {
    color: #fff;
    background-color: #FFD200;
    background-image: none !important;
    text-shadow: 0 0 1px rgba(0, 0, 0, 0.2);
}

/* headers of boxes */
.boxed-group > h3,
.boxed-group .heading {
    color: white;
    background-color: #1E64C8;
    border-radius: 3px 3px 0 0;
}

/**/
.mini-repo-list-item .repo-icon {
    color: #1E64C8;
}

.private .mini-repo-list-item .repo-icon,
.page-gist-edit .octicon-gist-secret {
    color: #FFD200;
}

.news .alert .octicon,
.page-gist-edit .octicon-gist {
    color: #1E64C8;
}


.notification-indicator .mail-status {
    display: none !important;
}

.notification-indicator[aria-label="You have unread notifications"] {
    color: #FFD200;
}
}
