css:
@-moz-document url-prefix("http://skycocker.github.io/chromebrew/") {
    body {
        background-color: #222;
        color: #f5f5f5;
    }
    .header {
        border-bottom-color: #444;
    }
    .container .jumbotron {
        background-color: #444;
        color: #f5f5f5;
    }
    .bs-example {
        background-color: #444;
        border-color: #777;
    }
    .highlight pre {
        background-color: #666;
        color: #f5f5f5;
        border-color: #777;
        border-radius: 0 0 4px 4px;
    }
    code, pre {
        background-color: #222;
        border: 1px solid #444;
        color: #f5f5f5;
    }
    .footer {
        border-top-color: #444;
    }
}
