css:
@-moz-document url-prefix("http://usermanual.clarizen.com") {
h2 a {
    font-size: 10px;
    float: right;
    color: #555;
}
.hiq-t H2 {
    border-bottom: 1px solid #ddd;
    padding: 10px 0px;
    margin: 10px 0px;
    font-weight: bold;
    color: #444;
}
.hiq-t table {
    border: 1px solid #C0C0C0;
    border-collapse: collapse;
    padding: 5px;
    font-size: 12px;
    margin: 10px 0px;
    width: 100%;
}
.hiq-t table th {
    border: 1px solid #C0C0C0;
    padding: 5px;
    text-align: left;
    background: #F0F0F0;
    font-weight: bold;
}
.hiq-t table td {
    border: 1px solid #C0C0C0;
    padding: 5px;
}
.ui-toc-tree .x-tree-node-el {
    font-size: 11px;
    font-family: Arial;
}
}
