css:
@-moz-document domain("graphql-explorer.githubapp.com"), domain("githubusercontent.com"), regexp("^https?://((gist|guides|help|raw|status|developer)\\.)?github\\.com/((?!generated_pages/preview).)*$") {
#files .file .data pre,
#files .file .line-data,
#files .file .line-number,
#readme div.plain pre {
    font-family: 'Fira Code', monospace !important;
    font-variant-ligatures: contextual !important;
    font-feature-settings: "calt" 1 !important;
}
.blob-wrapper,
.highlight,
.blob-code,
.blob-code-inner,
pre {
    font-family: 'Fira Code', monospace !important;
    font-variant-ligatures: contextual !important;
    font-feature-settings: "calt" 1 !important;
}
}
