css:
@-moz-document domain("services.planningcenteronline.com") {
* {
    -webkit-font-smoothing: antialiased !important;
    -moz-osx-font-smoothing: grayscale !important;
}
body {
    font-family: Lato;
}
div#wrap {
    max-width: 129.4em;
    margin: auto;
    background: #fafafa;
    height: calc(100vh);
  	box-shadow: 0px 0px 16px -6px rgba(0,0,0,0.35);
}
div#container {
    height: calc(100vh - 100px);
    overflow-y: scroll!important;
}
div#footer {
    max-width: 129em!important;
    margin: auto;
}
div[data-react-class*="Topbar"] {
    height: 60px !important;
}
div[data-react-class*="Topbar"] .simple {
    height: 60px !important;
}
div.simple :nth-child(4) {
    margin: auto !important;
}
div#last-viewed-plans {
    display: none!important;
}
}
