css:
@-moz-document domain("refactoring.com"), domain("martinfowler.com") {
html,
body {
  min-height: 100vh;
}

body {
  display: flex;
  flex-direction: column;
}

#banner,
#footer {
  width: 100%;
}

#banner,
#footer,
.navmenu {
  max-width: none;
  margin-left: 0;
}

#content {
  flex: 1;
  max-width: none;
  margin-left: auto;
  margin-right: auto;
}

#footer {
  min-height: 0;
  height: 40px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

#footer div.tw-logo,
#footer div.menu-button,
#footer div.copyright {
  position: static;
  top: auto;
  right: auto;
  bottom: auto;
  left: auto;
}

#footer div.tw-logo > a {
  display: block;
  height: 100%;
}

#footer div.copyright {
  margin-left: auto;
}

#footer div.menu-button {
  order: 1;
  margin: 0 20px;
}

#footer::after {
  content: none;
}
}
