css:
@-moz-document domain("docs.pixologic.com") {
.banner-mid {
  box-sizing: content-box;
  padding-top: 70px;
  height: 50px;
  background: hsl(0,0%,50%);
  transition: outline 0.2s;
  overflow: hidden;
}

.banner-mid > img {
  position: absolute;
  top: 0;
}

.search-container {
  top: auto;
}

#searchform input[type="text"] {
  padding: 5px;
}

#searchform input[type="submit"] {
  padding: 6px;
}

.banner-mid .container {
  margin-top: 0;
  display: none;
}

section {
  margin-top: 20px;
  padding-top: 0;
}

.site-header {
  padding-top: 0px;
  padding-bottom: 10px;
}

.main-navigation ul.nav-menu, .main-navigation div.nav-menu > ul {
  border-top: none;
}

.main-navigation ul.nav-menu, .main-navigation div.nav-menu > ul {
  margin-bottom: 10px;
}

.site-content, #secondary {
  margin-top: 0;
}

.widget-area .widget h3 {
  display: none;
}

.oclinks {
  margin-bottom: 10px;
}

.entry-header {
  margin-bottom: 0;
}

.entry-header .entry-title {
  margin-top: 0;
  margin-bottom: 10px;
}

.entry-content p, .entry-summary p, .comment-content p, .mu_register p {
  margin-bottom: .75rem;
}

.entry-content > ul > li > a {
  display: inline;
  padding: 0;
  font-size: inherit;
  font-weight: bold;
  text-transform: none;
  color: hsl(200,80%,40%);
  border: none;
}

.entry-content > ul > li > a:after {
  content: none;
}
}
