css:
@-moz-document domain(ion.tjhsst.edu) {
	/* your code goes here */

body {
    background-image: none;
    background-color: #23272a;
    color: #dedede;
}
.primary-content {
    background-color: #23272a;
    color: #dedede;
}
#activity-detail {
    background-color: #2c2f33;
    color: #dedede;
}
#activity-picker {
    background-color: #2c2f33;
    color: #dedede;
}
.announcement {
    background-color: #2c2f33
}
.widget {
    background-color: #454647
}
.widget-content {
    background-color: #454647
}
.widget-title {
    background-color: #2c2f33
}
h5,
h2,
h3,
h4 {
    background-color: #343536;
    color: #dedede;
}
#activity-picker .search-wrapper input {
    background-color: #343536;
    color: #dedede;
}
.search-wrapper {
    background-color: #343536;
    color: #dedede;
}
.block {
    color: #dedede;
}
.block active-block {
    background-color: #343536;
    color: black;
}
.nav li {
    background-color: #23272a;
    color: #dedede;
}
a {
    color: lightskyblue;
}
.header {
    background-color: #2c2c2c;
}

html, html * {
  color: #eeeeee !important;
  background-color: #222222 !important;
}

img, video {z-index: 1}
* {border-color: #555555 !important}
cite, cite * {color: #029833 !important}
:link, :link * {color: #8db2e5 !important}
input, textarea {background-color: #333333 !important}
a {background-color: rgba(255, 255, 255, 0.01) !important}
:visited, :visited * {color: rgb(211, 138, 138) !important}
html, body, input, select, button {background-image: none !important}

video {
  background-color: transparent !important;
}
}
