css:
@-moz-document domain("opswiki.squiz.net") {
/* Full page styles */

body, html {
 	background: #212121;
    color: #ffffff !important;
}

p, 
.info,
li,
ul {
 	color: #ffffff !important;   
}

a {
	color: #64B5F6 !important;
}

/* Buttons */



/* Header and navbar */
#header {
    background: #212121;
    padding-bottom: 10px;
}


/* Search box */
#searchinput {
    background: #424242;
    border: none !important;
    padding: 10px !important;
    width: 30vw !important;
    height: 15px !important;
    color: #fff;
}

/* Search box buttons */
#titlesearch {
    background: #424242;
    color: #fff;
    padding: 9px;
    border-radius: 0;
    border: none;
}

#fullsearch {
    background: #424242;
    color: #fff;
    padding: 9px;
    border-radius: 0;
    border: none;
}

#titlesearch:hover {
    background: #64B5F6 !important;
}

#fullsearch:hover {
    background: #64B5F6 !important;
}

/* Drop down menus */
select {
    background: #212121 !important;
    border: none !important;
    color: #fff !important;
    height: 25px !important;
}

/* Navbar */
.editbar, 
#message {
    background: #424242 !important;
    border: none !important;
    padding: 10px !important;
}

.wikilink {
    background: #212121 !important;
    border: none !important;
    padding: 10px !important;
    margin-top: 20px !important;
}

/* Current active tab in wikilinks */
.current {
    background: #424242 !important;
    border: none !important;
    padding: 10px !important;
    margin-top: 20px !important;
}

#pageline {
    background: #424242;
    border: none !important;
}

/* Page styles */
#page {
	background: #303030 !important;
}

/* Search stats element */
.searchstats {
	background: #303030 !important;
    color: #fff !important;
    border: none !important;
}


/* Code block synatx highlighting */
.line {
	color: #ffffff !important;
}

.Comment {
	color: #81C784 !important;
}

.ResWord {
    color: #e57373 !important;
}

.ID {
    color: #9E9E9E !important;
}

.String {
    color: #29B6F6 !important;
}

/* Content boxes set to darker background colour */
.tip,
.note,
.important,
.caution,
.warning,
.codearea,
pre,
.table-of-contents {
    background: #212121 !important;
    border: none !important;
    padding-top: 10px;
    color: #fff;
}

.red {
    background: #212121 !important;
}

/* Table styles */
td {
    background: #212121 !important;
    border-color: #424242 !important;
    padding-top: 10px;
    color: #fff !important;
}
}
