css:
@-moz-document domain("greasyfork.org") {
#main-header {
    background-color: #000 !important;
    color: #fff !important;
    background-image: linear-gradient(rgb(0, 0, 0), rgb(100, 100, 100)) !important;
}

#main-header .subtitle {
    text-shadow: -1px -1px 0px #2a2a2a, 1px -1px 0px #2a2a2a, -1px 1px 0px #2a2a2a, 1px 1px 0px #2a2a2a !important;
}

body {
    background: rgb(100, 100, 100) !important;
}

.width-constraint .text-content, ol.script-list, .list-option-group ul, #script-info, .script-author-description, code, .CodeRay, .line-numbers pre, .script-screenshots, textarea, input[type="text"], .add-screenshot-captions {
    background: #000 !important;
    color: lightgray !important;
}

.list-current {
    background: #4a4a4a !important;
}

a {
    color: gray !important;
}

a:visited {
    color: lightgray !important;
}

.install-link {
    color: white !important;
}
}
