css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Seven Familiar*/
/* i really want this to be global */
/*Images - pictures*/

#pet-headerPortrait-base
{
  background: url("https://pre00.deviantart.net/dee7/th/pre/f/2016/294/1/3/hhhh1_by_annettasassi-dalqbpc.png") -15px top no-repeat !important;
  background-size: auto 125px !important;
}

#pet-portrait
{
  background: url("https://img00.deviantart.net/4d41/i/2013/245/b/a/request__chibi_grell_by_trinitywolfdragon-d6kqadi.png") center no-repeat !important;
  background-size: auto 450px !important;
  height: 450px !important;
}

#pet-portrait > img[src*="/static/img/pet/icon/"]
{
  display: none !important;
}
/*Infos*/

#pet-info-name:before
{
  content: "Grell Sutcliff" !important;
  font-size: 20px !important;
}

#pet-info-name
{
  font-size: 0 !important;
}

#pet-info-evolve > .on
{
  font-size: 0 !important;
}

.pet-info-evolve-icon
{
  font-size: 13px !important;
}

.pet-info-evolve-icon:after
{
  content: " Grell nie może już ewoluować.";
}

#pet-info-racegender:before
{
  content: "Shinigami" !important;
  font-size: 16px !important;
  line-height: 20px !important;
}

#pet-info-racegender
{
  font-size: 0 !important;
  margin-top: 5px;
}

img[src*="/static/img/f.png"]
{
  background: url("http://www.eldarya.es/static/img/m.png") !important;
  padding-left: 14px !important;
  padding-top: 0px !important;
  width: 0 !important;
  height: 0 !important;
  margin-bottom: 5px !important;
}
/*Comida - food*/

#pet-info-food > img
{
  background: url("http://cliparts.co/cliparts/kcK/ord/kcKord9ki.png") !important;
  background-size: 60px 60px !important;
  margin-top: -5px !important;
  padding-left: 60px !important;
  padding-top: 60px !important;
  width: 0 !important;
  height: 0 !important;
}

#pet-info-food
{
  font-size: 0 !important;
}

#pet-info-food:before
{
  content: "Sebastian's Love" !important;
  font-size: 16px !important;
}

#pet-info-food > span
{
  font-size: 16px !important;
}
/*Seven en tu perfil*/

#player-display-pet
{
  background: url("https://img00.deviantart.net/4d41/i/2013/245/b/a/request__chibi_grell_by_trinitywolfdragon-d6kqadi.png") no-repeat;
  background-size: auto 100%;
  height: 390px !important;
  width: 450px !important;
}

#player-display-pet > img
{
  display: none !important;
}
