css:
@-moz-document domain("projectrik.com"), url-prefix("https://projectrik.com") {
body {
    background:#151515;
}

#pageHeader:not(.headerOnTop) {
    opacity:0.8;
}

.headerOnTop {
	background:#111;
    box-shadow:none;
}

.headerOnTop #topNav li a:link, .headerOnTop #topNav li a:visited {
    color:#ccc !important
}

#betaBanner {
	filter:invert(40%);
}

#wrap {
    box-shadow:0 0 0 6px  #2c2c2c;
    background:#222;
}

#pageFooter {
    box-shadow:0 0 0 6px  #2c2c2c;
    background:#222;
}

#pageFooter * {
	color:#555 !important;
}
#pageFooter *:hover {
	color:#999 !important;
}

input {
    background:#101010 !important;
    border-color:#333 !important;
}

.description, .newsPosted {
    border-color:#444 !important;
}

button, .description {
    background:#363636 !important;
}

.readTopic .topicTitle a:link, .readTopic .topicTitle a:visited {
    color:#bbb;
}
.topicTitle a:link, .topicTitle a:visited {
    color:#ddd;
}

.nav-links, .nav-links * {
    border-color:#333 !important;
}

.current {
	background:#444 !important;
}

a:link,a:visited {
    color:#4d97ca;
}
a:hover {
    color:#f63;
}

.record {
	border-bottom-color:#444;
    color:#aaa;
}

.sbbTitle, .right {
    color:#aaa !important;
}

.recordHeading {
	border-bottom-color:#666;
    color:#bbb;
}

.tabNav li.tnia {
    background:#1c1c1c;
}
.tabNav li.tnsel{
	background:#333;
}
.tabNav li.tnok{
    background:#505050;
    color:#fff;
}
.tabNav li.tnok:hover {
	background:#6a6a6a;
}
}
