css:
@-moz-document url-prefix("https://www.sspai.com") {
.btn {
	color: #292525;
}
header#header header .logo {
	display: block;
	position: absolute;
	left: 0;
	z-index: 1;
	background: url(https://www.sspai.com/public/images/logo_black.png) left center no-repeat;
	background-size: auto 100%;
	height: 38px;
	margin: 11px 0;
	width: 108px;
	font-size: 0;
}
header#header.second header .console a {
	color: #292525;
}
a.login {
	border-color: #292525;
	color: #292525;
}
header#header header {
	background-color: #fff;
	height: 60px;
	border-bottom: 1px solid #DBE2E8;
	transition: all 0.3s;
}
header#header header .console .search-form div input[type=text] {
	border-bottom: 1px solid #dae1e7;
	color: #292525;
}
}
