css:
 @-moz-document url(https://rooster.glu.nl) {
 .wc-cal-event {background-color: #252525}
.mytimetable-application-panel {background-color: #252525}

table td, select {color: white}
.wc-day-column.wc-today {background-color: #383838}
.wc-grid-timeslot-header {background-color: #313131; color: white}
.wc-hour-header.outside-business-hours {background-color: #e83636}
table.wc-time-slots {background-color: #252525}
.wc-header td {background-color: #313131; color: white}
.wc-day-column {border-left: 1px solid #383838}
.wc-hour-header.outside-business-hours {background-color: #313131; color: white}
.wc-time-slots {width: 0}
.wc-cal-day-event.color-0 .wc-cal-day-event-details, .wc-cal-event.color-0 .wc-time {background-color: #e83636}
::-webkit-scrollbar-thumb {background-color: #383838}

::-webkit-scrollbar-thumb {background-color: rgb(83, 83, 83)}
::-webkit-scrollbar-track {background-color: #313131}

.GKJMMQYCOI {background-color: #313131; color: white}
.GKJMMQYCII .GKJMMQYCMJ {background-color: black}
.GKJMMQYCJI .GKJMMQYCCK {color: white}
.GKJMMQYCOO {background-color: #252525}

.gwt-TabLayoutPanel .gwt-TabLayoutPanelTab {background-color: #252525}
.gwt-TabLayoutPanel .gwt-TabLayoutPanelTab .gwt-Label {color: grey}

.sidebar-content-panel-buttons {background-color: #252525}
.GKJMMQYCMO {background-color: #252525; color: white}
.GKJMMQYCNO {background-color: #252525; color: white}
.GKJMMQYCNO:before {background: #252525}
.GKJMMQYCLO {color: white}
.GKJMMQYCII label {color: white}
.sidebar-panel {background-color: #252525}

.gwt-Button[disabled], .gwt-Button[disabled]:hover, .gwt-PushButton-up-disabled, .gwt-PushButton-up-disabled:hover, .gwt-PushButton-down-disabled, .gwt-PushButton-down-disabled:hover {background-color: #313131; color: grey; border: 1px solid #252525}
.gwt-Button {background-color: #313131; color: grey}

.datePickerDays {background-color: #252525}
.datePickerDayIsWeekend {background-color: #313131}
.datePickerDayIsFiller {color: #8e8e8e}

#menu-top a, #menu-top a:visited, #menu-top a:active {color: #e83636}
.top-header-image {margin-left: 9999px}
 }
