css:
@-moz-document domain(www.facebook.com) {
body.home,
#sideNav:before,
#bluebarRoot > div:before,
.fbChatSidebar:before,
#sideNav:after,
#bluebarRoot > div:after,
.fbChatSidebar:after {
    background: url('https://images.unsplash.com/photo-1494756739853-38af58aa9dd0?ixlib=rb-0.3.5&q=85&fm=jpg&crop=entropy&cs=srgb&s=a87b2436d33bc75c4f38def68088f4c3');
    background-size: 100% auto;
    background-attachment: fixed;
    background-position: center;
}
#bluebarRoot > div {
    border-bottom: 1px solid #555555;
}
#bluebarRoot > div > div {
    position: fixed;
}
#sideNav {
	padding: 15px;
}
#sideNav,
#contentCol,
#bluebarRoot > div,
.fbChatSidebar {
    overflow: hidden;
    background: none !important;
}
#sideNav,
.fbChatSidebar {
    position: relative;
}
.fbChatSidebar {
    border: none !important;
}
#sideNav:before,
#bluebarRoot > div:before,
.fbChatSidebar:before {
    content: '';
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    position: absolute;
    filter: blur(10px);
    margin: -25px;
    z-index: -1;
    box-shadow: inset 0 0 0 3000px rgba(255, 255, 255, 0.3);
}
#sideNav:after,
#bluebarRoot > div:after,
.fbChatSidebar:after {
    content: '';
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    position: absolute;
    margin: -25px;
    z-index: -2;
}
.fbChatTypeahead * {
    border-radius: 0 !important;
}
#bluebarRoot [role="search"] {
    border: none !important;
    border-radius: 1px !important;
    left: 0 !important;
    top: 9px !important;
    height: 24px !important;
}

#bluebarRoot [role="search"] button {
    border-radius: 0 1px 1px 0 !important;
}
}
