@-moz-document domain("m.jeuxvideo.com")
/* ------------------------------------------- LISTE DES SUJET -------------------------------------- */
{
.bloc-languette .etage-2 {
    background: #20202F;
}
.bloc-languette .etage-1 {
    background: #20202F;
    color: white;
}
.liste-topics li:nth-of-type(2n+1) {
    background: #424251;
    border: 0px;
}
.liste-topics li:nth-of-type(2n) {
    background: #20202F;
    border: 0px;
}
.topic-inner .auteur {
    color: white;
}
.bloc-pagination-topics {
    background: #20202F;
}
.bloc-retour-haut {
    background: #424251;
}
.bloc-consultation {
    background: #20202F;
}
.contenu-fmobile {
    background: #424251;
    border-bottom: 0 solid transparent;
}
/* ------------------------------------------- TOPIC -------------------------------------- */
.post {
	color:#B8B8B8!important;
    background: #262626;

}
.post .contenu {
	color:#B8B8B8!important;
}

.liste-messages .post:nth-of-type(2n) {
    border: 0px solid #bcd2e9;
	color:#B8B8B8!important;
    background: #262626;
}
}
