css:
@-moz-document domain("xat.com") {
body,td,th {
	font-family: Century Gothic;
	color: #FFFFFF;
    text-shadow: 3px 3px 8px 2px #000000;
}
body {
	background-color: #000000;
  	font-family: Century Gothic;
  	text-shadow: 3px 3px 8px 2px #000000;
	BACKGROUND-POSITION: Center;
	BACKGROUND-ATTACHMENT: fixed;
	BACKGROUND-IMAGE: url("https://i.imgur.com/Ef8GWI6.png") !important;
  	BACKGROUND-SIZE: 100% 100% !important;
  	BACKGROUND-REPEAT: NO-REPEAT !important;
}

P {
	FONT-SIZE: 10px;
}
A:link {text-decoration: none; color:#FFFFFF}
A:visited {text-decoration: none; color:#FFFFFF}
A:hover {text-decoration: underline; color:#FFFFFF}
A:active {text-decoration: none; color:#FFFFFF}

.navbar-inner {
    background-color: #000000 !important;
  	background-image: none;
    box-shadow: 3px 3px 8px 2px #000000;
    border: 1px solid #FFFFFF;
}
  
#hero {
  	background: url("https://i.imgur.com/Ef8GWI6.png") !important;
  	background-size: 100% 490px !important;
  	background-repeat: no-repeat !important;
    background-color: #000000 !important;
    box-shadow: 3px 3px 8px 2px #000000;
    border: 1px solid #FFFFFF;
}

.btn-primary,.carousel-control {
    background-color: #000000;
  	background-image: none;
}
  
#hero .item p {
  	font-family: Century Gothic;
    font-size: 15px;
    color: #FFFFFF;
}

#groups .thumbnail {
  	background-color: #000000 !important;
  	border: 1px solid #FFFFFF;
  	box-shadow: 3px 3px 8px 2px #000000;
}

#groups .thumbnail .pic {
  	box-shadow: 3px 3px 8px 2px #000000;
}
#footer {
  	color: #FFFFFF !important;
  	background-image: none;
  	background-color: #000000;
    box-shadow: 3px 3px 8px 2px #000000;
    border: 1px solid #FFFFFF;
}
iframe{
  	display:none
}
embed{
    box-shadow: 3px 3px 8px 2px #000000;
    border: 1px solid #FFFFFF;
}
.foot {
    background-image: none;
    font-size: 10px;
    background-color: black;
}
}
