css:
@-moz-document domain(dvd.netflix.com) {

.qnav li a{
    margin-right:20px;
}
.queue-lists li {
    margin-bottom: 0 !important;
    padding: 4px 0 4px 3px !important;
}
.queue-lists li div.title {
    font-size: 11px;
}
.queue-lists li a{
    font-size: 13px !important;
}
.queue-lists li div.boxart,
.queue-lists li a.boxart
{
    width: 32px;
    height: 32px;
}
.move-handle {
    height:32px;
}

.queue-lists li div {
    font-size: 12px;
}

.queue-lists li .actionMenu {
    top: 10px;
}
}
