css:
@-moz-document url("http://www.tumblr.com/"), url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/")
{

  .post.new_post .new_post_label
  {
    background: none !important;
  }

  a.new_post_label > i
  {
    height: 90px;
    margin-top: -5px;
  }

  .new_post_label
  {
    padding: 71px 1px 8px 0 !important;
    font-size: 8pt !important;
    text-transform: lowercase;
  }

  .new_post_label/*_icon*/ > I
  {
    background: url('https://goo.gl/CrDjvp') no-repeat !important;
    z-index: -1;
  }

  .new_post_label > I
  {
    color: transparent !important;
    transition: background-position .08s linear !important;
  }

  #new_post_label_text > I
  {
    background-position: 2px -5px !important;
  }

  #new_post_label_photo > I
  {
    background-position: -73px -5px !important;
  }

  #new_post_label_quote > I
  {
    background-position: -146px -5px !important;
  }

  #new_post_label_link > I
  {
    background-position: -220px -5px  !important;
  }

  #new_post_label_chat > I
  {
    background-position: -297px -5px !important;
  }

  #new_post_label_audio > I
  {
    background-position: -373px -5px  !important;
  }

  #new_post_label_video > I
  {
    background-position: -450px -5px  !important;
  }
}
