@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.h-online.com") {

*[class*="ad"],
*[class*="banner"],
*[class*="cad"],
#header_ wrapper,
#bannerzone,
#leaderboard,
#heiseadvert,
#skyscraper {
display: none !important;
height:0 !important;
width:0 !important;
}

#header_wrapper {
margin-top:0 !important;
}

#newsheader,
#newsheader_left,
#newsheader_right {
background: #998 none !important;
}

#content,
#content_wrapper,
#frame_bottom {
background: #aa9 none !important;
}

}
