css:
@-moz-document regexp(".*myanimelist\\.net/anime/.*")
{

.page-common .borderDark {
	border-color: #cdcdcd;
	border-style: solid;
	border-width: 0 0 1px;
	display: none;
}

.page-forum .forum_boardrow2 {
	background-color: #f6f6f6;
	border-color: #ebebeb;
	border-style: solid;
	border-width: 0;
	padding: 6px 3px;
	display: none;
}

.page-forum .forum_boardrow1 {
	background-color: #fff;
	border-color: #ebebeb;
	border-style: solid;
	border-width: 0;
	padding: 6px 4px;
	vertical-align: top;
	display: none;
}

}

@-moz-document regexp(".*myanimelist\\.net/manga/.*")
{

.page-common .borderDark {
	border-color: #cdcdcd;
	border-style: solid;
	border-width: 0 0 1px;
	display: none;
}

.page-forum .forum_boardrow2 {
	background-color: #f6f6f6;
	border-color: #ebebeb;
	border-style: solid;
	border-width: 0;
	padding: 6px 3px;
	display: none;
}

.page-forum .forum_boardrow1 {
	background-color: #fff;
	border-color: #ebebeb;
	border-style: solid;
	border-width: 0;
	padding: 6px 4px;
	vertical-align: top;
	display: none;
}

}
