css:
@-moz-document domain("mercadolivre.com.br") {
li.results-item.article.stack {
    zoom: 90%;
}
@media (min-width: 1114px) {
    .search-results.stack li {
        height: 144px;
    }
}
.mclics {
    display: none;
}
.stack_column_item {
    display: inline-block;
}
.item.item--stack .item__stack_column {
    width: 100%;
}
.item__image {
    margin: 0px 12px 0px 10px;
}
.item__info-container {
    padding: 0;
}
.item.item--stack img {
    width: 120px;
    height: 120px;
}
.item.item--stack .item__info {
    display: block;
    height: 100px;
}
.item.item--stack .item__image {
    margin: 0 10px;
    height: 130px;
    min-height: 130px;
    width: 130px;
    min-width: 130px;
}
.item.item--stack .item__stack_column .stack_column_item {
    margin-bottom: -4px;
}
}
