@-moz-document domain(codepen.io) {

/*

CUSTOM CSS

.main-header {
    background: white;
    color: #555;
}
.main-header {
    border-bottom: 5px solid #ddd;
}
.button-editor-solid {
    color: #555;
    border: 3px solid #e4e4e4;
    background: #e4e4e4;
}
.button-editor-outline:hover, .button-editor-solid:hover {
    background: #ffffff;
}
.main-header {
    border-bottom: 5px solid #ddd;
    box-shadow: 0 1px 1px #e4e4e4;
}

*/


/* Quick Invert */

.main-header {
    filter: invert(100%);
}
#mini-personal-avatar, #mini-team-avatar {
    filter: invert(100%);
}
body.layout-top .top-boxes {
    filter: invert(100%);
}
body.layout-top .resizer, body.details .resizer {
    filter: invert(100%);
}

/* Home */
body {
    background: #ffffff;
}
.single-project, .single-pen {
    filter: invert(100%);
}
.iframe-wrap, .thumbnail-wrap {
    filter: invert(100%);
}

}
