css:
@-moz-document domain("gdax.com") {
body {
  --gdax-border-gray: #252b33;
  --gdax-black: #0E141D;
  --link-color-default: rgba(255, 255, 255, 0.4);
  --link-color-hover: rgb(255, 255, 255);
  --link-transition: all .1s ease-in-out;
}
.StatusBanner_status-view_2rGRs {
  border-bottom: 1px solid var(--gdax-border-gray);
}
.Navbar_row_1C-84 {
  background-color: var(--gdax-black) !important;
}
.Navbar_info-number-wrapper_VnPY0 {
  background-color: transparent !important;
}
.ProductSelection_menu-entry_3INfH {
  color: white !important;
}
.MarketInfo_market-num_1lAXs {
  opacity: 1 !important;
}
.MarketInfo_market-stat_2xWig,
.ProductSelection_description_jxdy8,
.MarketInfo_market-descr_2lp4B {
  color: white !important;
}
.MarketInfo_market-descr_2lp4B {
  opacity: 0.8 !important;
}
.ProductSelection_arrow_3QkdH:before,
.ProductSelection_arrow_3QkdH:after {
  background-color: white !important;
}
.Navbar_brand_2gRf8,
.NotificationsPanel_notifications-button_2FlFY,
.AccountPanel_sliding-button_2M6B0,
.AccountPanel_account-panel-user-name_2TNIL {
  filter: invert(100%) grayscale(100%) brightness(200%);
}
.AccountPanel_sliding-button_2M6B0:before {
  display: none;
}
.Navbar_home-link_2THCE {
  color: var(--link-color-default) !important;
  transition: var(--link-transition) !important;
}
.Navbar_home-link_2THCE:hover {
  color: var(--link-color-hover) !important;
  text-decoration: none !important;
}
}
