css:
@-moz-document url-prefix(https://mail.protonmail.com) {
	.toolbarMobile-container .pm_buttons, .toolbarMobile-container .pm_buttons a {
		height: 28px;
	}

	html.protonmail body.appConfigBody-is-mobile #body #pm_main {
		height: calc(100vh);
	}

	body.appConfigBody-is-mobile .headerSecuredMobile-container {
		height: 32px;
	}

	#conversation-view header h1 {
		font-size: 18px;
		padding-top: 3px;
		padding-bottom: 3px;
		letter-spacing: 0;
		line-height: 1.5em;
	}

	#conversation-view header h1 .starButton {
		top: 0;
	}

	#conversation-view header h1 .starButton a {
		font-size: 1.2rem;
	}

	/* Space in toolbar (message/thread selection, delete, filters etc.) */

	.toolbarMobile-container {
		height: 28px;
		height: 0;
		padding: 3px 0 3px;
	}

	/* Removal of horizontal lines */
	.pm_toolbar {
		box-shadow: 0 0 0;
	}

	#conversation-view header {
		border-bottom: 0;
	}

	/* Sender information */
	body.appConfigBody-is-mobile #conversation-view .message .summary {
		height: 55px;
	}

	/* Action buttons (filter, reply etc.) */
	#conversation-view .message .details {
		padding-bottom: 0;
	}

	#conversation-view .message .details .action-row {
		padding: 4px 0;
	}
}
