css:
@-moz-document regexp("https?://(www.)?wikiwand.*") {
/* i really want this to be global */

#main_menu.navbar_opaque 
{
	background-color: hsla(320, 4%, 32%, 1) !important;
}

div#content {
	color: #c0c0c0 !important;
	background-color: rgba(38, 38, 38, 1) !important;
}

body.light .infobox, body.light table.ww_ib_1 
{
    color: #888888 !important;
}

#wikiwand_toc 
{
	background-color: rgba(58, 58, 58, 1) !important;
}

#main_menu.navbar_opaque {
	background-color: hsla(320, 4%, 32%, 1) !important;
}

body:not(.dark) #header.fold #main_menu.navbar_opaque 
{
	background-color: rgb(84, 78, 82) !important;
}

#read_more a 
{
	background-color: rgba(13, 10, 12, 1) !important;
}

#main_menu>li.toc_folded {
	background-color: #544e52;
}

body.light #article_content_wrapper.padded, 
body.light #body_wrapp {
	color: #e0e0e0 !important;
	background-color: rgba(38, 38, 38, 1) !important;
}

#main_menu {
	background-color: rgba(84, 78, 82, 1);
}

body.light a, body.light a:visited 
{
    color: #ffaf50 !important;
}

body.light a:hover 
{
    color: #ffffa0 !important;
}

body.light .wikiwand_footer, div.ocd-content, div.ocd-content div.title
{
    background-color: #202020 !important;
    color: #eeeeee !important;
}

body.light .mw-headline
{
    color: #ffc47f !important;
    font-weight: 500 !important;
}


div.ocd-content div.text, .footer_invite_wrapper .invite_box h4
{
    color: #aaaaaa !important;
}

body.light .tooltipster-ocd
{
    border-color: #ffaf50;
}

.wiki_alert
{
    background-color: #181818 !important;
}

body.light table, body.light td, body.light tr
{
    background-color: #333333 !important;
    border-color: #666666 !important;
}


body.light th
{
    background-color: #4a403a !important;
}

}
