css:
@-moz-document regexp("https://fontawesome.com/v4\\.\\d+\\.\\d+/(icons|cheatsheet)/") {
/* clean */
div#banner, div.navbar.navbar-static-top,
div.jumbotron,div#social-buttons,p.lead,
#footer.footer{display:none !important}

/* icon size */
i.fa{font-size:2em !important;}
#search i.fa{font-size:1em !important}

/* Cheatsheet Adjustment */
#carbonads { float: none; margin-left: auto }
#carbonads::after { display: table; clear: both; content: "" }
.row > div {
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}
.row > div:hover {
    position: relative;
    z-index: 1;
    overflow: visible;
}
.row > div:hover,
.row > div:hover span { background-color: #fff }
}
