css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("qj.net") {
   div#gline, div.square > span#sqspan, div.top15, div.misc > div#v_ad.dSh, div.categories > center, iframe, ins, #konaLayers, #EBtopbanner {
      display: none !important;
   }
   div.contentholder div.content div.main div.misc div.dSh span#bspan, html > body > div.contentholder > div.content > div.main > div.categories, img[src*="wgp.jpg"],  img[src*="mss.png"] {
      display: none !important;
   }
   .com .bar#b0, #ccd0 {
      display: none !important;
   }
   html > body > center > div.toplinks {
      width: 500px !important;
      background-color: transparent !important;
      margin-bottom: 2px !important;
      padding: 0 50px 0 50px !important;
   }
   html > body > div.contentholder > div.content, .header {
      width: 600px !important;
   }
   .header {
      width: 598px !important;
   }
   html > body > div.contentholder {
      background-image: none !important;
   }
   html > body > div.contentholder > div.content > div.main {
      margin: auto !important;
      border-right: 1px solid black;
      border-left: 1px solid black;
   }
   .articles {
      float: none !important;
      margin: 1px auto !important;
   }
   .articles > table, .more .links {
      display: none !important;
   }
   .dropShadow {
      margin-bottom: 15px !important;
   }
   #iTxt img {
      margin-left: 10px !important;
      border: 0;
      max-width: 100% !important;
   }
   .kLink {
     color: #000 !important;
     text-decoration: none;
     border: 0 !important;
   }
   table#inArt td.iTxt table tr:last-child {
     display: none !important;
   }
   .next, .prev, .first {
      width: 100px !important;
      height: 0px !important;
      margin: 10px !important;
      font-family: verdana;
      font-size: 11px;
      float: left !important;
   }
   .next:after {
      content: "Next >";
   }
   .prev:after {
      content: "< Previous";
   }
   .first:after {
      content: "|< First";
   }
}
