css:
@-moz-document url-prefix("http://www.dmm.com/netgame/social/-/gadgets/=/app_id=854854/") {
div.dmm-ntgnavi{
  display:none;
}
 
#game_frame,
#area-game,
#page,
#main-ntg,
#w,
html,
body{
  width: 100% !important;
  height: 100% !important;
  margin: 0px;
/*   padding: 0px; */
}
}

@-moz-document regexp("^http://osapi.dmm.com/.*&aid=854854&.*") {
html,body{
  width: 100%;
  height: 100%;
}

div#globalNavi,
#spacing_top{
  display:none;
}

div#flashWrap,
div#externalswf,
embed#externalswf{
  width: 100% !important;
  height: 100% !important;
  max-width: 100% !important;
  max-height: 100% !important;
  margin-left: 0px !important;
  margin-right: 0px !important;
}
}

@-moz-document url-prefix("http://www.dmm.com/netgame/social/-/gadgets/=/app_id=854854/") {
body{
  min-width: 0px;
  margin: 0px;
  overflow:hidden;
}
}
