css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("calendar.google.com"), domain(forecast.io), domain(feed.mikle.com) {
  /* Google Calendar Increased Font Size */
  #calendarTitle {
    font-size: x-large;
  }

  div.day {
    font-size: large;
  }

  /* Weather Font Increase */
  div.fe_wind, span.fe_high_temp, span.fe_low_temp {
    font-size: medium;
  }

  /* RSS Feed Font Increase */
  div.feed_title a, div.feed_item_title a, div.feed_item_date {
    font-size: medium;
  }

  div.feed_item_description {
    font-size: large;
  }
}
