css:
@-moz-document url-prefix("https://www.findagrave.com") {
.tab-content .container {
    width: 750px;
}
.tab-content .col-md-5.col-lg-6.border-line {
    display: none;
}
.tab-content .col-sm-6,
.tab-content .col-lg-6 {
    width: 100%;
}
.tab-content .member-family {
    padding-left: 7px;
}
.tab-content .member-item {
    margin-top: 7px !important;
}
.tab-content .media-body p {
    display: inline-block;
}
.tab-content .media-body a {
    display: inline-block;
}
#memorial-summary.bg-dk .memorial-overview,
#memorial-summary.bg-dk .header-prad {
    background-color: #9195B8;
}
.section-bio-cover {
    margin-left: auto;
    margin-right: auto;
    max-width: 1100px;
}
.section-bio-cover .col-md-offset-1 {
    margin-left: 0;
}
.section-bio-cover .col-sm-3 {
    width: 30%;
}
.section-bio-cover .bio-dp img {
    object-position: 100% 100%;
}
.section-bio-cover .bio-dp,
.section-bio-cover .bio-dp img {
    height: auto !important;
}
.header.condensed ~ .header-prad {
    margin-top: 0 !important;
}
.header-prad > div,
.footer-prad > div {
    height: 0 !important;
}
.overview-panel .overview-panel--body .bio-min:first-child::first-line {
    font-size: inherit;
}
.overview-panel .overview-panel--body .bio-min.dropcap:first-child::first-letter {
    float: none;
    font-size: inherit;
    line-height: inherit;
    padding-top: 0;
    padding-right: 0;
    padding-left: 0;
}
#bio-overflow-text {
    display: inline !important;
}
.bio-more,
#sr-,
#flowersGrid,
.btn-msgs .form-control-static {
    display: none !important;
}
.section-bio-cover .row {
    display: flex;
}
.col-lg-offset-0 {
    width: 100% !important;
}
.col-lg-offset-3 {
    margin-left: 0% !important;
}
.col-lg-3 .photogrid-wrapper {
    width: 120% !important;
    position: relative;
}
.col-lg-6 {
    margin-left: 5% !important;
    align-self: flex-end;
    margin-bottom: 25px;
}
#partBio {
    display: none !important;
}
#fullBio {
    display: block !important;
}
}
