css:
@-moz-document url-prefix("http://hiking.sk"), url-prefix("https://hiking.sk") {
html,
body {
    height: 100%;
}
html {
    display: table;
    margin: auto;
}
body {
    display: table-cell;
    vertical-align: middle;
}
#main {
    width: 1200px;
}
#mainleft {
    width: 750px;
}
#hot_forums {
    max-height: 800px;
    overflow: scroll;
}
#links_caption,
#links,
.reklama2,
.reklama,
.googleAdsContainer,
.reklamapartners,
#hikeplanner_mini {
    display: none;
}
}
