css:
@-moz-document regexp('https://gitter.im.*') {
#chat-container,
.minibar {
    background: black;
}
.chat-item,
.chat-item__container {
    background: black;
}
.chat-item__action {
    color: white;
}
.trpContentPanel .chat-item:not(.unread) {
    background-color: black !important;
}
.right-toolbar.right-toolbar--collapsible {
    background: black;
    color: white;
}
.trpMoreBadge div {
    color: white;
}
#chat-input,
#chat-input-textarea,
#chat-input-wrapper {
    background: black;
}
}
