css:
@-moz-document domain("hdn.pt") {
body { color: #ffffff !important; background-color: #212121 !important; }
.container .jumbotron, .container-fluid .jumbotron { background-color: #212121 !important; border-color: #ed822a !important; }
.navbar { background-color: #212121 !important; border-width: 0 1px 4px 1px; }
.navbar-default { border-color: #e7e7e700 !important; }
.navbar-default .navbar-nav > li > a { color: #ffffff !important; }
.panel-body { background-color: #212121 !important; }
.list-group-item { background-color: #212121 !important; }
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 { color: #e3e3e3 !important; }
.label-primary { background-color: #5a5a5a !important; }
.label-yellow { background-color: #5a5a5a !important; }
.label-purple { background-color: #5a5a5a !important; }
.label-warning { background-color: #5a5a5a !important; }
.panel-warning > .panel-heading { color: #ffffff !important; background-color: #000 !important; border-color: #ed822a !important; }
.panel-info > .panel-heading { background-color: #000 !important; border-color: #ed822a !important; }
.panel { border: 1px solid #ed822a !important; }
.list-group-item { border: 1px solid #ed822a !important; }
.table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td { border: 1px solid #ed822a !important; }
.footer { border-top: 1px solid #ed822a !important; }
.panel-default > .panel-heading { color: #ffffff; background-color: #212121 !important; }
.well { background-color: #212121 !important; border: 1px solid #ed822a !important; }
.progress { background-color: #5a5a5a !important; border: 1px solid #ed822a !important; }
.progress-bar { color: #fff; background-color: #000 !important; }
.btn { color: #fff; border-color: #ed822a !important; background-color: #212121 !important; border-width: 1px 1px 1px 1px !important; }
.glyphicon { color: #fff !important; }
.track-entries { color: #d49f74; }
.modal-content { background-color: #212121 !important; border: 1px solid #ed822a !important; }
.form-control { background-color: #212121 !important; color: #ffffff }
}
